INSERT INTO events(name, description, date_start, date_end, category, photo)
VALUES
('Еретик', 'Церковная миссия приводит двух девушек к дверям обманчиво безобидного мистера Рида (Хью Грант). Не успев опомниться, они оказываются в его дьявольской ловушке, построенной с целью проверить их веру на прочность. Чтобы выжить, им придется пройти серию изощренных испытаний и столкнуться с тем, что не поддается рациональному объяснению. Теперь выбор, во что верить, для них означает выбор между жизнью и смертью', '2024-11-24', '2024-12-04', 'MOVIES', 'https://ms1.relax.by/images/20e977928c5e456fb1e69ef5e6546406/thumb/w%3D600%2Ch%3D900%2Cq%3D81/afisha_event_photo/e9/6e/de/e96ede7fa9f9191d0a445ca33636d4b5.jpg'),
('Мария', 'Сентябрь 1977 года. Мария Каллас, всемирно известная оперная дива на ранней пенсии, живет в роскошной парижской квартире с двумя пуделями, кухаркой и дворецким. Работа первой, помимо готовки, заключается в том, чтобы говорить Марии комплименты. Второй с утра до ночи передвигает для нее по дому огромный рояль, считает её таблетки, забирает её из ресторанов и пытается убедить побеседовать наконец не с галлюцинациями, а со специалистом', '2024-12-03', '2024-12-31', 'MOVIES', 'https://ms1.relax.by/images/20e977928c5e456fb1e69ef5e6546406/thumb/w%3D600%2Ch%3D900%2Cq%3D81/afisha_event_photo/a3/1c/35/a31c354c93bcbf69cb6f86826224c87b.jpg'),
('Гладиатор', 'После того как его дом завоевывают тиранические императоры, возглавляющие Рим, Луций, сын Луциллы и Максимуса, вынужден выйти на арену Колизея и обратиться к своему прошлому, чтобы найти в себе силы вернуть славу Рима его народу.', '2024-11-28', '2024-12-08', 'MOVIES', 'https://ms1.relax.by/images/20e977928c5e456fb1e69ef5e6546406/thumb/w%3D600%2Ch%3D900%2Cq%3D81/afisha_event_photo/94/ae/bc/94aebc3f647231640d7f31a897b0bc38.jpg'),
('Звездный час', '«Не боги горшки обжигают», «И мы не лыком шиты», да и знаменитое одесское: «Из всякого безвыходного положения есть, по меньшей мере, два выхода», - все это о новом спектакле Драматического театра белорусской армии', '2024-11-25', '2024-11-30', 'PERFORMANCES', 'https://dir.by/koncert/spectacle/zvezdnyj_chas/images/body_src_20240113_122543_4715.jpg'),
('Спектакль "Сильва"', '"Да здравствует любовь!" Именно под таким названием задумывалась новая оперетта венгерского композитора Имре Кальмана, теперь известная всем как "Сильва". Ее премьера состоялась в 1915 году на сцене венского "Иоганн Штраус-театра" и прошла триумфально: толпы зрителей буквально штурмовали кассы! Затем два года подряд спектакль шел ежедневно при полных залах даже несмотря на страшные события Первой мировой войны.', '2024-11-23', '2025-02-18', 'PERFORMANCES', 'https://dir.by/koncert/spectacle/silva/images/body_src_20200205_232821_3139.jpg'),
('Спектакль "Лучшая половинка меня"', 'Мужчина и женщина – кто они друг другу – союзники или враги? Какие тайные желания движут нами? И что будет, если признаться в них партнеру? Вечные вопросы и оригинальные ответы в спектакле «Лучшая половинка меня». Люди встречаются, люди влюбляются, и иногда…. Женятся. Иными словами, совершают совместный прыжок с парашютом, при котором в большинстве случаев их ждет…. Неизвестность. Они никогда ни в чем не уверены, но разве не в этом прелесть рискованных жизненных решений, ответственность за которые несут двое, плюс, давно сбивший свой прицел Купидон?', '2024-11-26', '2024-12-18', 'PERFORMANCES', 'https://dir.by/koncert/spectacle/luchshaya_polovinka_menya/images/body_src_20240129_151224_1629.jpg'),
('Рок-концерт Легенды оперы', '«Легенды Оперы» — легендарный проект, в котором звёзды Большого театра России исполняют популярные в мире оперные арии в современном звучании', '2024-11-26', '2024-11-30', 'CONCERTS', 'https://dir.by/koncert/opera/legendy/images/body_src_20241128_160943_4843.jpg'),
('Рок-опера "Юнона" и "Авось"', 'Поистине удивительна история графа Николая Петровича РЕЗАНОВА, положенная в основу сюжета рок-оперы "ЮНОНА" и "АВОСЬ", — история, которая трагически оборвалась на полдороге…Одержимый мечтой об осуществлении кругосветного плавания с целью открытия морского пути для удобного сообщения с новыми русскими владениями, граф Резанов подает одно прошение за другим с просьбой разрешить ему путешествие к берегам Калифорнии, "дабы Россия и Америка могли процветать в спокойном и полезном единении".', '2024-10-25', '2024-12-24', 'CONCERTS', 'https://dir.by/koncert/opera/yunona_i_avos/images/body_src_20240112_234246_3003.jpg');
